import { Injectable } from "@angular/core";

@Injectable({ providedIn: "root" })
export class MediaService {
    private readonly _videoSettings = {
        width: { ideal: 1920 },
        height: { ideal: 1080 }
    };

    public getVideoStream() : Promise<MediaStream> {
        return navigator.mediaDevices.getUserMedia({
            video: this._videoSettings
        });
    }

    public getVideoTrack() : Promise<MediaStreamTrack> {
        return navigator.mediaDevices.getUserMedia({
            video: this._videoSettings
        }).then((stream: MediaStream) => stream.getVideoTracks()[0]);
    }

    public static getVideoTrackId(stream: MediaStream): string {
        return stream.getVideoTracks()[0].id;
    }
}
