import { Injectable } from "@angular/core";
import { HubConnection } from "@microsoft/signalr";
import { Participant } from "src/model/participant";
import { SignalR } from "./signalr.utils";

@Injectable({ providedIn: "root" })
export class ConferenceService {
    private readonly _hubConnection: HubConnection;

    constructor() {
        this._hubConnection = SignalR.connection;
    }

    public getParticipants() : Promise<Participant[]> {
        return this._hubConnection.invoke<Participant[]>("GetParticipants");
    }

    public listenNewConnections(handler: (participant: Participant) => void) {
        this._hubConnection.on("OnNewConnection", handler);
    }

    public listenRemoveConnections(handler: (participant: Participant) => void) {
        this._hubConnection.on("OnRemoveConnection", handler);
    }
}
