import { Component, ElementRef, Input, ViewChild, AfterViewInit, OnInit, OnDestroy, Output } from '@angular/core';
import { Subject, Subscription } from 'rxjs';
import * as SimplePeer from 'simple-peer';
import { OnStreamEvent } from 'src/model/on-stream-event';
import { Participant } from 'src/model/participant';
import { RtcParticipant } from 'src/model/rtc-participant';
import { ConferenceService } from 'src/service/conference.service';
import { RtcService } from 'src/service/rtc.service';

@Component({
  selector: 'app-participant',
  templateUrl: './participant.component.html',
  styleUrls: ['./participant.component.scss'],
  providers: [ RtcService ]
})
export class ParticipantComponent implements OnInit, OnDestroy {
  @ViewChild("video") public videoEl! : ElementRef;
  @Input() public participant!: Participant;
  @Input("local-user-id") public localUserId!: string;

  private _subscriptions = new Subscription();

  public onStream = new Subject<MediaStream|null>();
  private _onStream$ = this.onStream.asObservable();

  public peer?: SimplePeer.Instance;
  public stream?: MediaStream|null;

  public isMicroActive = false;

  constructor(private readonly _rtcService: RtcService, private readonly _conferenceService: ConferenceService) {
    /*this._onStream$.subscribe((stream?: MediaStream|null) => {
      this.stream = stream;
      this._rtcService.createPeer(stream!, this.participant.id, true);
    });
    this._rtcService.onSignal$.subscribe((data: string) => {
      this._conferenceService.sendSignalToAll(data);
    });
    this._rtcService.onStream.subscribe((event: OnStreamEvent) => {
      this.onStream.next(event.data);
    });*/
    this._conferenceService.onSendSignal$.subscribe((signal: string) => {
      this._rtcService.signalPeer(this.peer!, this.participant.id, signal, this.stream);
    })
  }

  public ngOnInit(): void {
  }

  public ngOnDestroy(): void {
    this.peer?.destroy();
  }
}
