import { Injectable } from "@angular/core";

@Injectable({ providedIn: "root" })
export class MediaService {
    public getMediaStream() : Promise<MediaStream> {
        return navigator.mediaDevices.getUserMedia({
            video: {
                width: { ideal: 1366 },
                height: { ideal: 768 }
            }
        });
    }
}
