import { Injectable } from "@angular/core";
import { Subject } from "rxjs";
import * as SimplePeer from "simple-peer";
import { OnStreamEvent } from "src/model/on-stream-event";

@Injectable()
export class RtcService {
    private _onSignal = new Subject<string>();
    public onSignal$ = this._onSignal.asObservable();

    private _onStream = new Subject<MediaStream>();
    public onStream = this._onStream.asObservable();

    private _initPeerEvents(peer: SimplePeer.Instance) {
        peer.on('signal', (data: SimplePeer.SignalData) => {
            this._onSignal.next(JSON.stringify(data));
        });
    
        peer.on('stream', (stream: MediaStream) => {
        this._onStream.next(stream);
        });
    }

    public createPeer(stream: MediaStream|null, userId: string, initiator: boolean): SimplePeer.Instance {
        const peer = new (window as any)["SimplePeer"]({ initiator, stream });
        this._initPeerEvents(peer);
        return peer;
    }

    public createRemotePeer(initiator: boolean): SimplePeer.Instance {
        const peer = new (window as any)["SimplePeer"]({ initiator, trickle: false });
        this._initPeerEvents(peer);
        return peer;
    }

    public signalPeer(currentPeer: SimplePeer.Instance, userId: string, signal: string, stream: any) {
        const signalObject = JSON.parse(signal);
        if (currentPeer) {
            currentPeer.signal(signalObject);
        } else {
            currentPeer = this.createPeer(stream, userId, false);
            currentPeer.signal(signalObject);
        }
    }
}
