import { Component, Input, ViewChild, OnChanges, OnInit, ViewChildren, QueryList } from '@angular/core';
import { ParticipantComponent } from '../participant/participant.component';
import { Participant } from 'src/model/participant';
import { ConferenceService } from 'src/service/conference.service';
import { SignalR } from 'src/service/signalr.utils';

@Component({
  selector: 'app-conference-main',
  templateUrl: './conference-main.component.html',
  styleUrls: ['./conference-main.component.scss']
})
export class ConferenceMainComponent implements OnInit {
  @Input() public userId!: string;
  @Input() public userName!: string;

  @ViewChild("currentParticipant") public currentParticipant! : ParticipantComponent;
  @ViewChildren(ParticipantComponent) public participantElements! : QueryList<ParticipantComponent>;

  public participants: Participant[] = [];

  constructor(private readonly _conferenceService: ConferenceService) {}

  private _onNewParticipant(participant: Participant): void {
    this.participants.push(participant);
  }

  private _onRemoveParticipant(participant: Participant): void {
    let index = this.participants.findIndex(p => p.id === participant.id);
    this.participants.splice(index, 1);
  }

  private _onToggleMicrophone(isActive: boolean, userId: string) {
    let participantCmp = this.participantElements.find(c => c.participant.id === userId);
    if (participantCmp) {
      participantCmp.isMicroActive = isActive;
    }
  }

  private _initEvents(): void {
    this._conferenceService.listenEvent("OnNewConnection", this._onNewParticipant.bind(this));
    this._conferenceService.listenEvent("OnRemoveConnection", this._onRemoveParticipant.bind(this));
    this._conferenceService.listenEvent("OnToggleMicrophone", this._onToggleMicrophone.bind(this));
  }

  public ngOnInit() {
    SignalR.connect()
      .then(() => SignalR.startConnection(this.userId, this.userName))
      .then(() => this._conferenceService.getParticipants())
      .then((participants: Participant[]) => {
        this.participants = participants;
      });
    this._initEvents();
  }
}
