import { Injectable } from "@angular/core";

@Injectable({ providedIn: "root" })
export class MediaService {
    private readonly _videoSettings = {
        width: { ideal: 1920 },
        height: { ideal: 1080 }
    };
    private readonly _audioFrequency = 440;

    public getVideoStream() : Promise<MediaStream> {
        return navigator.mediaDevices.getUserMedia({
            video: this._videoSettings
        });
    }

    public getVideoTrack() : Promise<MediaStreamTrack> {
        return navigator.mediaDevices.getUserMedia({
            video: this._videoSettings
        }).then((stream: MediaStream) => stream.getVideoTracks()[0]);
    }

    public static getVideoTrackId(stream: MediaStream): string {
        return stream.getVideoTracks()[0].id;
    }

    public getAudioTrack() : MediaStreamTrack {
        const audioContext = new window.AudioContext();

        const oscillator = audioContext.createOscillator();
        oscillator.frequency.value = this._audioFrequency;
        oscillator.connect(audioContext.destination);

        return audioContext.createMediaStreamDestination().stream.getAudioTracks()[0];
    }
}
