import { Component, Input, ViewChild, OnChanges, OnInit, ViewChildren, QueryList, AfterViewInit, AfterContentInit, AfterViewChecked, OnDestroy } from '@angular/core';
import { ParticipantComponent } from '../participant/participant.component';
import { Participant } from 'src/model/participant';
import { ConferenceService } from 'src/service/conference.service';
import { InputControlsComponent } from '../input-controls/input-controls.component';
import { ToggleMicroEvent } from 'src/model/toggle-micro';
import { Subscription } from 'rxjs';
import { RtcParticipant } from 'src/model/rtc-participant';

@Component({
  selector: 'app-conference-main',
  templateUrl: './conference-main.component.html',
  styleUrls: ['./conference-main.component.scss']
})
export class ConferenceMainComponent implements OnInit, OnDestroy {
  private _subscriptions = new Subscription();

  public userId!: string;
  public userName!: string;

  @ViewChild("controls") public inputControls! : InputControlsComponent;
  @ViewChildren(ParticipantComponent) public participantElements! : QueryList<ParticipantComponent>;

  public participants: RtcParticipant[] = [];


  constructor(
    private readonly _conferenceService: ConferenceService) {
    this.userId = crypto.randomUUID();
    this.userName = this._getNameFromCookies();
    this._initEvents();
  }

  private _initEvents(): void {
    this._subscriptions.add(this._conferenceService.onConnected$.subscribe(this._onConnectedHandler.bind(this)));
    this._subscriptions.add(this._conferenceService.getParticipants$.subscribe(this._getParticipantsHandler.bind(this)));
    this._subscriptions.add(this._conferenceService.onNewConnection$.subscribe((participant: Participant) => {
      this.participants.push(new RtcParticipant(new MediaStream(), participant.id, participant.name));
    }));
    this._subscriptions.add(this._conferenceService.onRemoveConnection$.subscribe((participant: Participant) => {
      let index = this.participants.findIndex(p => p.id === participant.id);
      this.participants.splice(index, 1);
    }));
    this._subscriptions.add(this._conferenceService.onToggleMicrophone$.subscribe((event: ToggleMicroEvent) => {
      let participantCmp = this.participantElements.find(c => c.participant.id === event.userId);
      if (participantCmp) {
        participantCmp.isMicroActive = event.isActive;
      }
    }));
  }

  private _getNameFromCookies(): string {
    let matches = document.cookie.match(new RegExp(
      "(?:^|; )" + "name".replace(/([\.$?*|{}\(\)\[\]\\\/\+^])/g, '\\$1') + "=([^;]*)"
    ))
    return decodeURIComponent(matches![1]);
  }

  private async _onConnectedHandler() {
    console.log(`Id: ${this.userId} Name: ${this.userName} - connected succesfully`);
    this.participants.push(new RtcParticipant(await this._getMediaStream(), this.userId, this.userName));
    this._conferenceService.getParticipants();
  }

  private async _getParticipantsHandler(participants: Participant[]) {
    this.participants = [ this.participants[0], ...participants.map(p => new RtcParticipant(new MediaStream(), p.id, p.name)) ];
    //await this._createOffer();
  }

  private async _getMediaStream(): Promise<MediaStream> {
    return navigator.mediaDevices.getUserMedia({
      video: true,
      audio: false
    });
  }

  public ngOnInit() {
    this._conferenceService.startConnection(this.userId, this.userName);
  }

  public ngOnDestroy(): void {
    this._subscriptions.unsubscribe();  
  }
}
