import { Component, ElementRef, Input, ViewChild, OnInit, OnDestroy, Output } from '@angular/core';
import { Subject, Subscription } from 'rxjs';
import { AnswerEvent } from 'src/model/answer-event';
import { OfferEvent } from 'src/model/offer-event';
import { RtcParticipant } from 'src/model/rtc-participant';
import { ConferenceService } from 'src/service/conference.service';
import { RtcService } from 'src/service/rtc.service';

@Component({
  selector: 'app-participant',
  templateUrl: './participant.component.html',
  styleUrls: ['./participant.component.scss']
})
export class ParticipantComponent implements OnInit, OnDestroy {
  @ViewChild("video") public videoEl! : ElementRef;
  @Input() public participant!: RtcParticipant;

  private _subscriptions = new Subscription();

  public peerConnection: RTCPeerConnection = new RTCPeerConnection();

  public isMicroActive = false;

  constructor(private readonly _rtcService: RtcService, private readonly _conferenceService: ConferenceService) {
  }

  private _initOnAnswer() {
    this._conferenceService.onAnswer$.subscribe(async (answerEvent: AnswerEvent) => {
      if (answerEvent.userId !== this.participant.id) {
        return;
      }
      let answer = JSON.parse(answerEvent.answerJson) as RTCSessionDescriptionInit;
      console.log('Add answer triggerd')
      console.log('answer:', answer)
      if (!this.peerConnection.currentRemoteDescription){
        this.peerConnection.setRemoteDescription(answer);
      }
    });
  }

  public ngOnInit() {
    let stream = this.participant.stream;
    stream.getTracks().forEach((track) => {
      debugger;
      this.peerConnection.addTrack(track, stream);
    });
    this._initOnAnswer();
    this.peerConnection.ontrack = (event) => {
      event.streams[0].getTracks().forEach((track) => {
        this.participant.stream.addTrack(track);
      });
    };
  }

  public ngOnDestroy(): void {
    this.peerConnection.close();
    this._subscriptions.unsubscribe();
  }
}
