import { Injectable } from "@angular/core";
import { Subject } from "rxjs";
import * as SimplePeer from "simple-peer";

@Injectable({ providedIn: "root" })
export class RtcService {
    private _initPeerEvents(peer: SimplePeer.Instance, onSignal: Subject<string>, onStream: Subject<MediaStream>) {
        peer.on('signal', (data: SimplePeer.SignalData) => {
            onSignal.next(JSON.stringify(data));
        });
    
        peer.on('stream', (stream: MediaStream) => {
            onStream.next(stream);
        });
    }

    public createPeer(stream: MediaStream|null, userId: string, initiator: boolean, onSignal: Subject<string>, onStream: Subject<MediaStream>): SimplePeer.Instance {
        const peer = new (window as any)["SimplePeer"]({ initiator, stream });
        this._initPeerEvents(peer, onSignal, onStream);
        return peer;
    }

    public createRemotePeer(initiator: boolean, onSignal: Subject<string>, onStream: Subject<MediaStream>): SimplePeer.Instance {
        const peer = new (window as any)["SimplePeer"]({ initiator, trickle: false });
        this._initPeerEvents(peer, onSignal, onStream);
        return peer;
    }

    public signalPeer(currentPeer: SimplePeer.Instance, userId: string, signal: string, stream: any) {
        const signalObject = JSON.parse(signal);
        if (currentPeer) {
            currentPeer.signal(signalObject);
        } else {
            currentPeer = this.createPeer(stream, userId, false);
            currentPeer.signal(signalObject);
        }
    }
}
