import { Injectable } from "@angular/core";
import { Participant } from "src/model/participant";
import { Subject } from "rxjs";

@Injectable({ providedIn: "root" })
export class ConferenceService {
    private _getParticipants = new Subject<Participant[]>()
    public getParticipants$ = this._getParticipants.asObservable();

    private _toggleMicrophone = new Subject<boolean>();
    public toggleMicrophone$ = this._toggleMicrophone.asObservable();

    private _newConnection = new Subject<Participant>();
    public newConnection$ = this._newConnection.asObservable();

    private _removeConnection = new Subject<Participant>();
    public removeConnection$ = this._removeConnection.asObservable();
}
