import { Injectable } from "@angular/core";
import { Subject } from "rxjs";
import { SignalRService } from "./signalr.service";

@Injectable({ providedIn: "root" })
export class RtcService {
  private readonly _peerConnectionConfig =  {
    iceServers: [
      { urls: "stun:stun.services.mozilla.com" },
      { urls: "stun:stun.l.google.com:19302" },
      { urls: "stun:stun.awt.be:3478" },
      { urls: "stun:stun.b2b2c.ca:3478" },
      { urls: "stun:stun.bahnhof.net:3478" },
      { urls: "stun:stun.barracuda.com:3478" },
      { urls: "stun:stun.bluesip.net:3478" },
      { urls: "stun:stun.bmwgs.cz:3478" },
      { urls: "stun:stun.botonakis.com:3478" },
      { urls: "stun:stun.budgetphone.nl:3478" },
      { urls: "stun:stun.cablenet-as.net:3478" }
    ]
  };

  private _onIceCandidate = new Subject<RTCPeerConnectionIceEvent>();
  public onIceCandidate$ = this._onIceCandidate.asObservable();

  constructor(private readonly _signalR: SignalRService) {}

  public createPeer(stream: MediaStream, outputStream: MediaStream, userSrcId: string, userDestId: string): RTCPeerConnection {
    let peer = new RTCPeerConnection(this._peerConnectionConfig);
   
    stream.getTracks().forEach((track) => {
      peer.addTrack(track, stream);
    });
    peer.onicecandidate = (event) => {
      if(event.candidate != null) {
        this._signalR.sendCandidate(userSrcId, userDestId, event.candidate);
      }
    };
    peer.ontrack = (event) => {
      event.streams[0].getTracks().forEach((track) => {
        outputStream.addTrack(track);
      });
    };
    return peer;
  }

  public async getMediaStream(): Promise<MediaStream> {
    let stream = await navigator.mediaDevices.getUserMedia({
      video: {
        width: { max: 800 },
        height: { max: 600 }
      },
      audio: false
    });
    stream.getVideoTracks().forEach((track) => track.stop());
    return stream;
  }
}
