import { Injectable } from "@angular/core";
import { Subject } from "rxjs";

@Injectable({ providedIn: "root" })
export class RtcService {
  private readonly _peerConnectionConfig =  {
    iceServers: [
      { urls: "stun:stun.services.mozilla.com" },
      { urls: "stun:stun.l.google.com:19302" },
    ]
  };

  private _onIceCandidate = new Subject<RTCPeerConnectionIceEvent>();
  public onIceCandidate$ = this._onIceCandidate.asObservable();

  public createPeer(stream: MediaStream, outputStream: MediaStream|null): RTCPeerConnection {
    let peer = new RTCPeerConnection(this._peerConnectionConfig);
   
    peer.onicecandidate = (event) => {
      if(event.candidate != null) {
        console.log('SENDING ICE');
        this._onIceCandidate.next(event);
      }
    }

    stream.getTracks().forEach((track) => {
      peer.addTrack(track);
    });

    if (outputStream) {
      peer.ontrack = (event) => {
        event.streams[0].getTracks().forEach((track) => {
          outputStream.addTrack(track);
        });
      };
    }  
    return peer;
  }

  public async getMediaStream(): Promise<MediaStream> {
    return navigator.mediaDevices.getUserMedia({
      video: {
        width: { ideal: 800 },
        height: { ideal: 600 }
      },
      audio: false
    });
  }
}
