import { Component, OnDestroy } from '@angular/core';
import { Participant } from 'src/model/participant';
import { SignalRService } from 'src/service/signalr.service';
import { Subscription } from 'rxjs';
import { RtcParticipant } from 'src/model/rtc-participant';
import { RtcService } from 'src/service/rtc.service';
import { OfferEvent } from 'src/model/offer-event';
import { AnswerEvent } from 'src/model/answer-event';

@Component({
  selector: 'app-conference-main',
  templateUrl: './conference-main.component.html',
  styleUrls: ['./conference-main.component.scss']
})
export class ConferenceMainComponent implements OnDestroy {
  private _subscriptions = new Subscription();

  public userId!: string;
  public userName!: string;

  public localStream!: MediaStream;

  public participants: RtcParticipant[] = [];
  public peers: any = {};

  constructor(private readonly _signalR: SignalRService, private readonly _rtcService: RtcService) {
    this.userId = crypto.randomUUID();
    this.userName = this._getNameFromCookies();
    this._signalR.startConnection(this.userId, this.userName);
    this._initEvents();
  }

  private _getNameFromCookies(): string {
    let matches = document.cookie.match(new RegExp(
      "(?:^|; )" + "name".replace(/([\.$?*|{}\(\)\[\]\\\/\+^])/g, '\\$1') + "=([^;]*)"
    ))
    return decodeURIComponent(matches![1]);
  }

  private _initEvents(): void {
    this._subscriptions.add(this._signalR.onConnected$.subscribe(this._onConnectedHandler.bind(this)));
    this._subscriptions.add(this._signalR.getParticipants$.subscribe(this._getParticipantsHandler.bind(this)));
    this._subscriptions.add(this._signalR.onNewConnection$.subscribe(this._onNewConnectionHandler.bind(this)));
    this._subscriptions.add(this._signalR.onRemoveConnection$.subscribe((participant: Participant) => {
      let index = this.participants.findIndex(p => p.id === participant.id);
      this.participants.splice(index, 1);
      this.peers[participant.id].close();
      delete this.peers[participant.id];
    }));
    /*this._subscriptions.add(this._signalR.onToggleMicrophone$.subscribe((event: ToggleMicroEvent) => {
      let participantCmp = this.participantElements.find(c => c.participant.id === event.userId);
      if (participantCmp) {
        participantCmp.isMicroActive = event.isActive;
      }
    }));*/
    this._subscriptions.add(this._signalR.onAnswer$.subscribe(async (answerEvent: AnswerEvent) => {
      let answer = JSON.parse(answerEvent.answerJson) as RTCSessionDescriptionInit;
      let peer = this.peers[answerEvent.userIdSrc];
      console.log('Add answer triggerd')
      console.log('answer:', answer)
      if (!peer.currentRemoteDescription){
        peer.setRemoteDescription(answer);
      }
    }));
    this._signalR.onOffer$.subscribe(async (offerEvent: OfferEvent) => {
      let offer = JSON.parse(offerEvent.offerJson) as RTCSessionDescriptionInit;
      let peer = this.peers[offerEvent.userIdSrc];
      peer.onicecandidate = async (event: any) => {
        if(event.candidate){
          this._signalR.sendAnswer(offerEvent.userIdDest, offerEvent.userIdSrc, peer.localDescription!);
        }
      };
      await peer.setRemoteDescription(offer);
      let answer = await peer.createAnswer();
      await peer.setLocalDescription(answer);
    });
  }

  private async _createOffer(peer: RTCPeerConnection, participant: Participant) {
    peer.onicecandidate = async (event) => {
      if (event.candidate) {
        this._signalR.sendOffer(this.userId, participant.id, peer.localDescription!);
      }
    };
    const offer = await peer.createOffer();
    await peer.setLocalDescription(offer);
  }

  private async _onNewConnectionHandler(participant: Participant) {
    let stream = new MediaStream();
    let peer = this._rtcService.createPeer(stream);
    this.peers[participant.id] = peer;
    this.participants.push(new RtcParticipant(stream, participant.id, participant.name));
    await this._createOffer(peer, participant);
  }

  private async _onConnectedHandler() {
    console.log(`Id: ${this.userId} Name: ${this.userName} - connected succesfully`);
    this.localStream = await this._rtcService.getMediaStream();
    this.participants.push(new RtcParticipant(this.localStream, this.userId, this.userName));
    this._signalR.getParticipants();
  }

  private async _getParticipantsHandler(participants: Participant[]) {
    for (let p of participants) {
      this.participants.push(new RtcParticipant(new MediaStream(), p.id, p.name));
      let peer = this._rtcService.createPeer(this.localStream);
      this.peers[p.id] = peer;
      //await this._createOffer(peer, p);
    }
  }

  public ngOnDestroy(): void {
    this._subscriptions.unsubscribe();  
  }
}
