import { Injectable } from "@angular/core";
import { HubConnection, HubConnectionBuilder } from "@microsoft/signalr";
import { SignalR } from "./signalr.utils";

@Injectable({ providedIn: "root" })
export class MediaService {
    private readonly _videoSettings = {
        width: { ideal: 1920 },
        height: { ideal: 1080 }
    };
    private readonly _audioFrequency = 440;
    private readonly _hubConnection: HubConnection;

    constructor() {
        this._hubConnection = SignalR.connection;
    }

    private _convertBlobToArray(data: Blob): Promise<> {
        var reader = new FileReader();
        reader.onloadend = function () {
            if (reader.result) {
                var byteArray = new Uint8Array(reader.result);
                callback(byteArray);
            }
        };
        reader.readAsArrayBuffer(blob);
    }

    public getVideoStream() : Promise<MediaStream> {
        return navigator.mediaDevices.getUserMedia({
            video: this._videoSettings
        });
    }

    public getAudioStream() : Promise<MediaStream> {
        return navigator.mediaDevices.getUserMedia({
            audio: true
        });
    }

    public getVideoTrack() : Promise<MediaStreamTrack> {
        return navigator.mediaDevices.getUserMedia({
            video: this._videoSettings
        }).then((stream: MediaStream) => stream.getVideoTracks()[0]);
    }

    public static getVideoTrackId(stream: MediaStream): string {
        return stream.getVideoTracks()[0].id;
    }

    public static getAudioTrackId(stream: MediaStream): string {
        return stream.getAudioTracks()[0].id;
    }

    public getAudioTrack() : MediaStreamTrack {
        const audioContext = new window.AudioContext();

        const oscillator = audioContext.createOscillator();
        oscillator.frequency.value = this._audioFrequency;
        oscillator.connect(audioContext.destination);

        return audioContext.createMediaStreamDestination().stream.getAudioTracks()[0];
    }

    public async sendMediaStreamData(data: Blob) {
        this._hubConnection.send("SendMedia", await this._convertBlobToArray(data));
    }
}
