import { HubConnection, HubConnectionBuilder } from "@microsoft/signalr";
import constants from "src/constants";

export class SignalR {
    public static connection: HubConnection = new HubConnectionBuilder()
        .withUrl(constants.serverUrl + "/conference").build();
        
    public static connect() : Promise<void> {
        return SignalR.connection.start();
    }

    public static startConnection(userId: string, userName: string) : Promise<void> {
        SignalR.connection.on("Connected", () => {
            console.log(`Id: ${userId} Name: ${userName} - connected succesfully`);
            SignalR.connection.off("Connected");
        });
        return SignalR.connection.send("Connect", { id: userId, name: userName });
    }
}
