import { MediaService } from 'src/service/media.service';

@Component({
  selector: 'app-participant',
  templateUrl: './participant.component.html',
  styleUrls: ['./participant.component.scss']
})
export class ParticipantComponent {
  @ViewChild("video") public videoEl! : ElementRef;
  public mediaRecorder?: MediaRecorder;

  private _handleMediaInput(message: MessageEvent<any>) {
    debugger;
    console.log(message);
  }

  constructor(private readonly _mediaService: MediaService) {
    this._mediaService.listenMediaData(this._handleMediaInput);
  }
}
